(239, 'h3', '« C''est surtout de l''humain quoi »', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', NULL, '', 19, 11),
(240, 'citation_image', '', '', '', '', '', '', 'Il faut que la population soutienne les commerçants, on ne peut pas vivre juste du tourisme', '', '', '', '67658a70819602.79369699.jpg', '', '', '', '', '', '', NULL, 'Romuald Renault est installé dans sa boulangerie depuis sept', 20, 5),
