INSERT INTO `chapitre` (`id_chapitre`, `titre`, `chapo`, `image`, `alt`) VALUES
(1, 'Battre la campagne', 'Le monde rural se décline au pluriel. Défense d’un terroir, sauvegarde d’une langue régionale ou préservation de l’écosystème local… En ruralité, les populations se mobilisent pour conserver leur identité, quitte à tenir tête aux élu·es et aux industriels.', '6765984bb632d3.25971507.jpg', ''),
(2, 'Cultiver les liens ', 'En France, un tiers de la population réside en milieu rural. Des territoires souvent dépourvus de commerces ou de services publics. Élu·es, bénévoles et commerçant·es tentent de changer la donne mais se heurtent au manque de moyens et à des habitant·es pas toujours réceptif·ves. ', '6765981ea3ad36.35725560.jpg', ''),
(3, 'Irriguer la lutte ', 'Pour améliorer leurs conditions de travail, des berger·es montent un syndicat et des agricultrices isolées s’entraident. Ailleurs, des ouvrier·es s’inquiètent des fermetures d’usines dans des campagnes longtemps dépendantes du secteur industriel. ', '6765982e9155d9.57722978.jpg', ''),
(4, 'Test (pas touche)', 'zadefrgthy', '', '');
